CREATE TABLE `CATEGORIAS` (
  `CodCategoria` char(6) NOT NULL,
  `NombreCategoria` varchar(20) DEFAULT NULL
) ENGINE=InnoDB DEFAULT CHARSET=latin1;


CREATE TABLE `COMENTARIOS` (
  `IdComentario` int(11) NOT NULL,
  `CodUsuario` char(7) NOT NULL,
  `IdEntrada` char(7) NOT NULL,
  `Comentario` varchar(3000) DEFAULT NULL
) ENGINE=InnoDB DEFAULT CHARSET=latin1;


CREATE TABLE `ENTRADAS` (
  `IdEntrada` char(7) NOT NULL,
  `Titulo` varchar(100) CHARACTER SET utf8 COLLATE utf8_spanish_ci NOT NULL,
  `ImagenEntrada` varchar(100) DEFAULT NULL,
  `FechaCreacion` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP,
  `Contenido` blob,
  `Publicado` char(1) DEFAULT 'N',
  `UltimaModificacion` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP,
  `CodUsuario` char(7) NOT NULL
) ENGINE=InnoDB DEFAULT CHARSET=latin1;


CREATE TABLE `PERTENECE` (
  `IdEntrada` char(7) NOT NULL,
  `CodCategoria` char(6) NOT NULL
) ENGINE=InnoDB DEFAULT CHARSET=latin1;


CREATE TABLE `USUARIOS` (
  `CodUsuario` char(7) NOT NULL,
  `Usuario` varchar(25) DEFAULT NULL,
  `ImgUsuario` varchar(50) DEFAULT NULL,
  `Nombre` varchar(25) DEFAULT NULL,
  `Apellidos` varchar(50) DEFAULT NULL,
  `Sexo` char(1) DEFAULT NULL,
  `FechaNacimiento` date DEFAULT NULL,
  `CorreoElectronico` varchar(100) DEFAULT NULL,
  `TipoUsuario` varchar(13) DEFAULT NULL,
  `Contraseña` varchar(32) DEFAULT NULL,
  `Tema` varchar(50) DEFAULT 'default'
) ENGINE=InnoDB DEFAULT CHARSET=latin1;




ALTER TABLE `CATEGORIAS`
  ADD PRIMARY KEY (`CodCategoria`);


ALTER TABLE `COMENTARIOS`
  ADD PRIMARY KEY (`IdComentario`,`CodUsuario`,`IdEntrada`),
  ADD KEY `en_co` (`IdEntrada`),
  ADD KEY `us_co` (`CodUsuario`);

ALTER TABLE `ENTRADAS`
  ADD PRIMARY KEY (`IdEntrada`,`CodUsuario`),
  ADD KEY `us_en` (`CodUsuario`);

ALTER TABLE `PERTENECE`
  ADD PRIMARY KEY (`IdEntrada`,`CodCategoria`),
  ADD KEY `ca_pe` (`CodCategoria`);

ALTER TABLE `USUARIOS`
  ADD PRIMARY KEY (`CodUsuario`);

ALTER TABLE `COMENTARIOS`
  MODIFY `IdComentario` int(11) NOT NULL AUTO_INCREMENT, AUTO_INCREMENT=51;

ALTER TABLE `COMENTARIOS`
  ADD CONSTRAINT `en_co` FOREIGN KEY (`IdEntrada`) REFERENCES `ENTRADAS` (`IdEntrada`) ON DELETE CASCADE ON UPDATE CASCADE,
  ADD CONSTRAINT `us_co` FOREIGN KEY (`CodUsuario`) REFERENCES `USUARIOS` (`CodUsuario`) ON DELETE CASCADE ON UPDATE CASCADE;

ALTER TABLE `ENTRADAS`
  ADD CONSTRAINT `us_en` FOREIGN KEY (`CodUsuario`) REFERENCES `USUARIOS` (`CodUsuario`) ON DELETE CASCADE ON UPDATE CASCADE;

ALTER TABLE `PERTENECE`
  ADD CONSTRAINT `ca_pe` FOREIGN KEY (`CodCategoria`) REFERENCES `CATEGORIAS` (`CodCategoria`) ON DELETE CASCADE ON UPDATE CASCADE,
  ADD CONSTRAINT `en_pe` FOREIGN KEY (`IdEntrada`) REFERENCES `ENTRADAS` (`IdEntrada`) ON DELETE CASCADE ON UPDATE CASCADE;


